    public static int[][] getlvl(int[][] lvl){
        int[][] lvln = new int[lvl.length][lvl[0].length];
        for(int i=0;i<lvl.length;i++){
            for(int j=0;j<lvl[i].length;j++){
                lvln[i][j] = lvl[i][j];
            }
        }
        return lvln;
    }
    
    public static class LevelDetail{
        private int[][] level;
        private int xstart,ystart;
        private int health;
        private int key;
        //private ArrayList<Entity> entity;

        public LevelDetail(int[][] level, int xstart, int ystart, int health, int key) {
            this.level = level;
            this.xstart = xstart;
            this.ystart = ystart;
            this.health = health;
            this.key = key;
            //this.entity = entity;
        }

        public int getHealth() {
            return health;
        }

        public int getKey() {
            return key;
        }

        public int[][] getLevel() {
            return level;
        }

        public int getXstart() {
            return xstart;
        }

        public int getYstart() {
            return ystart;
        }
        
        
        
    }
    
